import React from 'react'
import PropTypes from "prop-types"
import FilterButton from "./filter-button"

const Filter = ({ category, handleCategoryChange }) => {
  const [categories, setCategories] = React.useState([
    {
      id: "develop-code",
      name: "Develop Code"
    },
    {
      id: "enable-experiences",
      name: "Enable Experiences"
    },
    {
      id: "build-brands",
      name: "Build Brands"
    },
    {
      id: "design-products",
      name: "Design Products"
    },
    {
      id: "create-art",
      name: "Create Art"
    },
    {
      id: "cook-food",
      name: "Cook Food"
    },
    {
      id: "write-articles",
      name: "write-articles"
    },
    {
      id: "make-music",
      name: "Make Music"
    }
  ])
  return (
    <>
      <div className="container">
        {categories.map(c => (
          <FilterButton key={c.id} title={c.name} isActive={c.id === category} handleClick={handleCategoryChange} />
        ))}
      </div>
      <style jsx>
        {`
          .container {
            width: 100%;
            display: flex;
            overflow: auto;
            flex-wrap: wrap;
            max-width: 800px;
            align-items: center;
            padding-top: 10px;
            border-width: 0;
            padding-left: 20px;
            padding-right: 20px;
            flex-direction: row;
            justify-content: flex-start;
            border-bottom-width: 0;
          }
        `}
      </style>
    </>
  )
}

Filter.propTypes = {
  category: PropTypes.string,
  handleCategoryChange: PropTypes.func
}

export default Filter
