import React from 'react'
import PropTypes from "prop-types"
import FilterButton from "./filter-button"

const Filter = ({ category, handleCategoryChange }) => {
  const [categories, setCategories] = React.useState([
    "Develop Code",
    "Enable Experiences",
    "Build Brands",
    "Design Products",
    "Create Art",
    "Cook Food",
    "Write Articles",
    "Make Music"
  ])
  return (
    <>
      <div className="container">
        {categories.map((c, index) => (
          <FilterButton title={c} isActive={c === category} handleClick={handleCategoryChange} />
        ))}
      </div>
      <style jsx>
        {`
          .container {
            width: 100%;
            display: flex;
            overflow: auto;
            flex-wrap: wrap;
            max-width: 800px;
            align-items: center;
            padding-top: 10px;
            border-width: 0;
            padding-left: 20px;
            padding-right: 20px;
            flex-direction: row;
            justify-content: flex-start;
            border-bottom-width: 0;
          }
        `}
      </style>
    </>
  )
}

Filter.propTypes = {
  category: PropTypes.string,
  handleCategoryChange: PropTypes.func
}

export default Filter
