import React from 'react'
import Head from 'next/head'

import Header from '../components/header'
import Title from '../components/title'
import Filter from '../components/filter'
import ThingList from '../components/thing-list'
import Quote from '../components/quote'
import Footer from '../components/footer'
import Main from "../components/main"

const Home = () => {
  const categoryAll = "Do Things"
  const quoteDefault = "Always The Next One"
  const [category, setCateogry] = React.useState(null) 
  const [title, setTitle] = React.useState(categoryAll)
  const [quote, setQuote] = React.useState(quoteDefault)
  const [data, setData] = React.useState(null)

  const handleCategoryChange = (cat) => {
    if(cat !== category) {
      setCateogry(cat)
    } else {
      setCateogry(categoryAll)
    }
  }

  // React.useEffect(() => {
  //   const getData = await fetch(`/test/${category}`).catch(error => {
  //     console.log(error)
  //   })

  //   if(getData) {
  //     setData(getData)
  //   }

  // }, [category])

  React.useEffect(() => {
    if(category) {
      setTitle(category)
    } else {
      setTitle(categoryAll)
    }
  }, [category])


  return (
    <>
      <div className="container">
        <Head>
          <title>Do Things | Utoft Studios</title>
          <meta property="og:title" content="Do Things | Utoft Studios" />
        </Head>
        <Header></Header>
        <Main>
          <Title title={`I ${title}`}></Title>
          <Filter category={category} handleCategoryChange={handleCategoryChange}></Filter>
          <ThingList data={data}></ThingList>
          <Quote quote={quote}></Quote>
        </Main>
        <Footer></Footer>
      </div>
      <style jsx>
        {`
          .container {
            width: 100%;
            display: flex;
            overflow: auto;
            min-height: 100vh;
            align-items: center;
            flex-direction: column;
            justify-content: flex-start;
          }
        `}
      </style>
    </>
  )
}

export default Home
