import React from "react"
import PropTypes from "prop-types"

const Button = ({ label, options }) => {
  return (
    <>
      <button className="thqButton button">{label}</button>
      <style jsx>
        {`
          .button {
            color: #ffffff;
            width: ${options.fullWidth ? "100%" : "auto"};
            display: block;
            padding: 10px;
            align-self: center;
            text-align: center;
            border-color: transparent;
            border-width: 0;
            background-color: #130930;
          }
        `}
      </style>
    </>
  )
}
Button.defaultProps = {
  label: "label",
  options: {
    fullWidth: false,
    color: "primary"
  }
}

Button.propTypes = {
  label: PropTypes.string,
  options: PropTypes.objectOf({
    fullWidth: PropTypes.bool,
    color: PropTypes.oneOf(["primary", "secondary"])
  })
}

export default Button
